    $(crop_canvas).insertAfter(effect_canvas);
    $(input_img).insertAfter(crop_canvas);

    // hide the guts
    input_img.hide();
    crop_canvas.hide();
    $(effect_canvas).hide();
    
    // get contexts and txtures
    crop_context = crop_canvas[0].getContext('2d');
    
    // default filter
    self.select_filter(0);

    if (isMobileDevice) {
      CameraTag.fire(dom_id, "initialized");
    }
